mapper.select:

SELECT QNA_NO, TITLE, CONTENT, QNA_DATE, ID, ANSWER, RESPON,PROCESS
FROM QNA
WHERE QNA_NO = #{QNA_NO}


SELECT EMP_CON_NO, TITLE, CONTENT, EC_DATE, ID, ANSWER, RESPON,PROCESS
FROM EMP_CONSULTING
WHERE EMP_CON_NO = #{EMP_CON_NO}

mapper.insert:
INSERT INTO BOARD_FILE ( BOARD_FILENO, BOARD_NO, BOARD_FILENAME, BOARD_DATE ) SELECT BOARD_FILENO.NEXTVAL, A.* FROM (
SELECT (SELECT MAX(BOARD_NO) FROM F_BOARD) AS BOARD_NO, #{item} AS BOARD_FILENAME, SYSDATE AS BOARD_DATE FROM DUAL
) A

INSERT INTO BOARD_FILE ( BOARD_FILENO, BOARD_NO, BOARD_FILENAME, BOARD_DATE ) SELECT BOARD_FILENO.NEXTVAL, A.* FROM (
SELECT #{param1} AS BOARD_NO, #{item} AS BOARD_FILENAME, SYSDATE AS BOARD_DATE FROM DUAL
) A
