mapper.select:
SELECT PROGRAM_NO, TITLE, CONTENT, S_DATE, E_DATE
FROM ( SELECT PROGRAM_NO, B_NO, TITLE, CONTENT, FILENAME, S_DATE, E_DATE, ROW_NUMBER() OVER(ORDER BY PROGRAM_NO) AS RNUM FROM PROGRAM WHERE 1=1
) WHERE RNUM BETWEEN #{rowStart} AND #{rowEnd} ORDER BY PROGRAM_NO ASC

mapper.delete:

delete from PROGRAM_FILE
where PROGRAM_NO = #{PROGRAM_NO}
and EMP_FILE = #{EMP_FILE}

mapper.update:
update board_Notice
set
noti_title=#{noti_title},
noti_context=#{noti_context}
where
noti_NO=#{noti_NO}

choose.when:
update noti_file
set up_fileName =
#{item.up_fileName}
where
noti_NO=#{item.noti_NO}
and up_fileNO =
#{item.up_fileNO}

mapper.select:
SELECT JOB_NO, COMPANY, TITLE, JOB_D, S_DATE, D_DATE
FROM ( SELECT JOB_NO, CAREER, EDU, AREA, SAL, EMP_TYPE, WORK_TYPE, WELFARE, COMPANY, B_TYPE, SCALE, ESTABLISH, Y_SALES, HP, EMP_NUM, S_DATE, D_DATE, JOB_D, TITLE, ROW_NUMBER() OVER(ORDER BY JOB_NO) AS RNUM FROM JOB_INFO WHERE 1=1
) WHERE RNUM BETWEEN #{rowStart} AND #{rowEnd} ORDER BY JOB_NO ASC
