mapper.update:

UPDATE C_REG
SET TITLE = #{TITLE}, CONTENT = #{CONTENT}
WHERE REGI_NO = #{REGI_NO}

mapper.select:
SELECT UNI_B_NO, B_NAME, U_NAME, CASE WHEN TO_CHAR(E_DATE, 'YYYY-MM-DD')>TO_CHAR(SYSDATE, 'YYYY-MM-DD') THEN '진행중' WHEN TO_CHAR(E_DATE, 'YYYY-MM-DD')<TO_CHAR(SYSDATE, 'YYYY-MM-DD') THEN '마감' END AS STATE FROM ( SELECT UNI_B_NO, MG_NO, U_NAME, UNI_ADDR, UNI_HP, B_NAME, B_CONTENT, PERMISSION, B_EXPENSE, S_DATE, E_DATE, ROW_NUMBER() OVER(ORDER BY UNI_B_NO) AS RNUM FROM UNI_BUSINESS WHERE 1=1
)
WHERE RNUM BETWEEN #{rowStart} AND #{rowEnd} ORDER BY UNI_B_NO ASC


SELECT NAME, ADDR, HP, EMAIL 
FROM ST_MEMBER SM, MEMBER M 
WHERE M.ID='gkrtod' AND SM.ID=#{param1}


SELECT B.UNI_B_NO
	FROM UNI_BUSINESS B, APPLICATION A 
	WHERE A.UNI_B_NO=B.UNI_B_NO AND ID = #{param1}
